(() => {
  const existing = document.querySelector('script[data-axiomind="htmx"]');
  if (existing) {
    return;
  }

  if (window.htmx) {
    return;
  }

  const script = document.createElement('script');
  script.src = 'https://unpkg.com/htmx.org@1.9.12';
  script.defer = true;
  script.crossOrigin = 'anonymous';
  script.dataset.axiomind = 'htmx';
  document.head.appendChild(script);
})();
